function Plugin() {
	
	this.id			= -1;
	this.pid		= -1;
	
	this.name 		= name;
	this.author		= author;
	this.version	= version;
	
	this.active		= false;
	this.priority	= 0;
}

Plugin.prototype.__run = function(pid, im) {
	
	this.pid = pid;
	this.active = true;
	
	console.log('Run Plugin', this.name + '@' + this.version, '(pid:', this.pid + ')');
}

Plugin.prototype.__stop = function() {
	
	this.pid = -1;
	this.active = false;
	
	console.log('Stop Plugin', this.name + '@' + this.version);
}

module.exports = Plugin;