var RConClient 	= require("srcds-tools").RConClient;
var Logger		= require("srcds-logs");

function Server(host, ports, password) { // ports[0] rcon port | ports[1] log port
	
	this.rcon = new RConClient();
	this.rcon.connect(host, ports[0]);
	this.rcon.sendPassword(password);
	
	this.logger = new Logger(ports[1]);
	
	this.host = host;
	this.port = ports[0];
	
	this.active = false;
}

Server.prototype.__start = function() {
	
	this.active = true;
}

Server.prototype.__stop = function() {
	
	this.active = false;
}

module.exports = Server;