function ServerManager() {
	
	this.servers = [];
}

ServerManager.prototype.add = function(host, port, password) {
	
	this.servers.push(new Server(host, port, password));
}

ServerManager.prototype.remove = function(host) {
	
	for(var i = 0; i < this.server.length; i++) {
		
		var server = this.servers[i];
		
		if(server.host == host)
			this.servers.splice(i, 1);
	}
}

ServerManager.prototype.search = function() {
	
	for(var i = 0; i < this.server.length; i++) {
		
		var server = this.servers[i];
		
		if(!server.active)
			return server;
	}
	
	return null;
}